import { Controller } from "@hotwired/stimulus"

export default class extends Controller {
  static targets = ["salary", "inss", "address", "neighborhood", "city", "state"]

  connect() {
    console.log("Stimulus controller connected")
  }

  changeSalary(event) {
    // Formata o salário corretamente
    this.salaryTarget.value = this.currency(this.salaryTarget.value);

    // Remove R$, pontos e vírgulas do valor e converte para número decimal
    let value = encodeURIComponent(this.salaryTarget.value);

    // Converte para string limpa, sem caracteres adicionais  
    console.log(value, this.salaryTarget.value);

    const url = `/collaborators/call/${value}`;
    fetch(url)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.text();
      })
      .then(data => {
        // Formata a resposta do INSS para o formato monetário
        this.inssTarget.value = this.currency(data);
      })
      .catch(error => {
        console.error('There has been a problem with your fetch operation:', error);
      });
  }

  currency(value) {
    // Remove a formatação existente e converte para número
    value = Number(value.replace(/\./g, "").replace("R$", "").replace(",", "."));

    // Formata no padrão brasileiro com separadores corretos
    return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(value);
  }

  maskCPF(event) {
    console.log('worked')
    let cpf = event.target.value
    cpf = cpf.replace(/\D/g, "")
    cpf = cpf.replace(/(\d{3})(\d)/, "$1.$2")
    cpf = cpf.replace(/(\d{3})(\d)/, "$1.$2")
    cpf = cpf.replace(/(\d{3})(\d{1,2})$/, "$1-$2")
    event.target.value = cpf
  }

  maskPhone(event) {
    let value = event.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,5})(\d{0,4})/)
    event.target.value = !value[2] ? value[1] : '(' + value[1] + ') ' + value[2] + (value[3] ? '-' + value[3] : '')
  }

  maskCep(event) {
    let value = event.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,3})(\d{0,3})/)
    event.target.value = !value[2] ? value[1] : value[1] + '.' + value[2] + (value[3] ? '-' + value[3] : '')
  }

  getAddress(event) {
    console.log('its working')
    let cep = event.target.value.replace(".", "").replace("-", "")
    if (cep.length != 8) return

    const url = `https://viacep.com.br/ws/${cep}/json/`
    fetch(url)
      .then(response => response.json())
      .then(data => {
        this.addressTarget.value = data.logradouro
        this.cityTarget.value = data.localidade
        this.neighborhoodTarget.value = data.bairro
        this.stateTarget.value = data.uf
      })
  }
}
