import { Controller } from "@hotwired/stimulus"

// Constants for URL endpoints
const INSS_CALCULATION_URL = '/collaborators/calculate_inss_discount/';
const CEP_API_URL = 'https://viacep.com.br/ws/';

export default class extends Controller {
  static targets = ["salary", "inss", "address", "neighborhood", "city", "state", "form"]

  connect() {
    // Initialization code if needed
  }

  changeSalary(event) {
    event.preventDefault();

    const formattedValue = this.formatCurrencyForRequest(this.salaryTarget.value);
    this.updateInssDiscount(formattedValue);
  }

  async updateInssDiscount(salary) {
    try {
      const response = await fetch(`${INSS_CALCULATION_URL}${encodeURIComponent(salary)}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.text();
      this.inssTarget.value = this.formatCurrency(data);
    } catch (error) {
      console.error('Error updating INSS discount:', error);
      // Handle error appropriately
    }
  }

  formatCurrencyForRequest(value) {
    return value.replace("R$", "").replace(/\./g, "").trim();
  }

  formatCurrency(value) {
    const number = Number(value.replace(/\./g, "").replace("R$", "").replace(",", "."));
    return new Intl.NumberFormat('pt-BR', { style: 'currency', currency: 'BRL' }).format(number);
  }

  maskCPF(event) {
    const cpf = event.target.value.replace(/\D/g, "")
      .substring(0, 11)
      .replace(/(\d{3})(\d)/, "$1.$2")
      .replace(/(\d{3})(\d)/, "$1.$2")
      .replace(/(\d{3})(\d{1,2})$/, "$1-$2");
    event.target.value = cpf;
  }

  maskPhone(event) {
    const value = event.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,5})(\d{0,4})/);
    event.target.value = value[2] ? `(${value[1]}) ${value[2]}${value[3] ? '-' + value[3] : ''}` : value[1];
  }

  maskCep(event) {
    const value = event.target.value.replace(/\D/g, '').match(/(\d{0,2})(\d{0,3})(\d{0,3})/);
    event.target.value = value[2] ? `${value[1]}.${value[2]}${value[3] ? '-' + value[3] : ''}` : value[1];
  }

  async getAddress(event) {
    const cep = event.target.value.replace(/\D/g, '');
    if (cep.length !== 8) return;

    try {
      const response = await fetch(`${CEP_API_URL}${cep}/json/`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      this.addressTarget.value = data.logradouro;
      this.cityTarget.value = data.localidade;
      this.neighborhoodTarget.value = data.bairro;
      this.stateTarget.value = data.uf;
    } catch (error) {
      console.error('Error fetching address:', error);
      // Handle error appropriately
    }
  }

  submitForm(event) {
    event.preventDefault();
    this.convertFieldsToDecimal();
    this.formTarget.submit(); // Enviar o formulário
  }

  convertFieldsToDecimal() {
    this.salaryTarget.value = this.parseCurrency(this.salaryTarget.value);
    this.inssTarget.value = this.parseCurrency(this.inssTarget.value);
  }

  parseCurrency(value) {
    return value.replace("R$", "").replace(/\./g, "").replace(",", ".").trim();
  }
}
